  #{skuTitle,jdbcType=VARCHAR}, #{skuParam,jdbcType=VARCHAR}, #{skuPicture,jdbcType=VARCHAR}, 
  #{num,jdbcType=INTEGER}, #{price,jdbcType=DECIMAL}, #{discountPrice,jdbcType=DECIMAL}, 
